import Link from 'next/link'
import { useRouter } from 'next/router'
import Languages from '../Languages'

const Menu = ({ headerSections }) => {

  const router = useRouter();
  const navLinks = ["/home/was","/now","/archive","/book"]
  const navLinksMicro = ["/home","/now","/archive","/book"]

  const openMenu = (headerSections, setLang, currentLang) => {
      document.querySelector('.menu').classList.add('menu--open');
  }

  return (
    <div className="menu">
      <nav className="menu__nav">
        <ul>
          {
            headerSections.map((name, index) => (
              <li className={`menu__nav__li${router.pathname.indexOf(navLinksMicro[index]) >= 0 ? ' menu__nav__li--active':''}`}>
                <Link href={navLinks[index]} onClick={openMenu}>{name}</Link>
              </li>
            ))
          }
        </ul>
      </nav>
      <Languages setLang={setLang} currentLang={lang} />
    </div>
  );
};

export default Menu;
