import EventList from '../components/Now/EventList';
import Theme from '../components/Now/Theme';

const Now = ({ themes, events }) => {

  const currentTheme = themes.filter((theme) => theme.content.Timing[0] === "future")

  return (
    <div className="now">
      <Theme theme={currentTheme}/>
      <EventList events={currentEvents}/>
    </div>
  )
}

export async function getServerSideProps({ preview = null }) {
  const themes = (await getThemes(preview)) || []
  const events = (await getEventList(preview)) || []
  return {
      props: { themes, events },
  }
}

export default Now
