async function fetchAPI(query, { variables, preview, folder } = {}) {
  const res = await fetch('https://gapi.storyblok.com/v1/api', {
      method: 'POST',
      headers: {
          'Content-Type': 'application/json',
          Token: 'aYu5zqsg2dHE3YmFmsDgFAtt',
          Version: preview ? 'draft' : 'published',
          starts_with: folder ? folder : '/',
      },
      body: JSON.stringify({
          query,
          variables,
      }),
  })

  const json = await res.json()
  if (json.errors) {
      console.error(json.errors)
      throw new Error('Failed to fetch API')
  }
  return json.data
}

// Frontpage
export async function getHome() {
  const data = await fetchAPI(`
  {
    HomeItem(id: "home") {
        id
        name
        content {
            Title
            Subtitle
            Introduction_video {
              filename
            }
            Storylink {
              url
            }
            Wo_ar
            Wo_de
            Wo_en
            Was_ar
            Was_de
            Was_en
            Wer_ar
            Wer_de
            Wer_en
            Wie_ar
            Wie_de
            Wie_en
            Warum_ar
            Warum_de
            Warum_en
            Impressum
        }
    }
  }     
  `,
  {
      preview: true
  }
  )
  return data?.HomeItem.content
}

// Events
export async function getEvents(path) {
    const data = await fetchAPI(`
    {
        ArchivedeventItem(id: "archived-events/${path}") {
          id
          content {
            Text1
            Text2
            Text3
            Title1
            Title2
            Title3
            Video1 {
                filename
                alt
            }
            Video2 {
                filename
                alt
            }
            Video3 {
                filename
                alt
            }
            Photo1 {
                filename
            }
            Photo2 {
                filename
            }
            Photo3 {
                filename
            }
            Audio1 {
                filename
                alt
            }
            Audio2 {
                filename
                alt
            }
            Audio3 {
                filename
                alt
            }
            date
            Feedback_medias {
                filename
            }
            thumbnail {
                filename
            }
          }
        }
      } 
    `,
    {
        preview: true,
    }
    )
    return data?.ArchivedeventItem.content
  }

  export async function getThemes(path) {
    const data = await fetchAPI(`
    {
        ThemeItems {
          items {
            content {
                Title
                Description
                Timing
                events {
                  date
                  location
                  thumbnail {
                    filename
                  }
                  stream_link {
                    url
                  }
                  registration_email {
                    email
                  }
                  description
                }
            }
          }
        }
      } 
    `,
    {
        preview: true,
    }
    )
    return data?.ThemeItems.items
  }

  export async function getEventList(path) {
    const data = await fetchAPI(`
    {
        ArchivedeventItems {
          items {
            content {
              theme {
                name
              }
              date
              thumbnail {
                filename
              }
            }
            full_slug
          }
        }
      }

    `,
    {
        preview: true,
    }
    )
    return data?.ArchivedeventItems.items
  }
