const EventToolBar = ({ media, setMedia }) => {

    return (
      <div className="event__toolbar">
        <button className={`archive__toolbar__button${archiveMode === 'place' ? ' archive__toolbar__button--selected':''}`} onClick={() => setMedia('place')}>ORDER BY PLACE</button>
        <button className={`archive__toolbar__button${archiveMode === 'theme' ? ' archive__toolbar__button--selected':''}`} onClick={() => setMedia('theme')}>ORDER BY THEME</button>
      </div>
    );
  };

  export default EventToolBar;
