    // }
  }, [router.query.homeSection])

  return (
    // <MainText title={content[lang].titles[index]} content={content[lang].content['storyfeld']} next={pages[next]} location={router.query.homeSection} lang={lang}/>
    <h1>{router.query.homeSection, content}</h1>
  )
}
