import React, { useEffect, useState } from 'react';

import Header from './Header';
import Languages from './Languages';
import Menu from './Menu';
import { getHome } from '../lib/api'
import content from '../data';

const Layout = ({ children, setLang, lang, fetchedContent }) => {

    // const impressumText = fetchedContent.Impressum;
    const impressumText = 'salu'

    return (
        <div className="container">


            <Header headerSections={content[lang].header} lang={lang}/>
            <Languages setLang={setLang} currentLang={lang}/>
            <Menu headerSections={content[lang].header} setLang={setLang} lang={lang} impressum={impressumText}/>
            {children}
        </div>
    )
}

export async function getServerSideProps({ preview = null }) {
    const fetchedContent = (await getHome(preview)) || []
    return {
        props: { fetchedContent },
    }
}

export default Layout
