import { useEffect, useState } from 'react'
import { getThemes, getEventList, getHome } from '../lib/api'

import ArchiveList from '../components/Archive/ArchiveList'
import ArchiveLocations from '../components/Archive/ArchiveLocations'
import locations from '../locations';

const Archive = ({ lang, themes, events }) => {

  useEffect(() => {
    document.querySelector('.menu').classList.remove('menu--open');
  })

  const [selectedLocation, setSelectedLocation] = useState(0)

  const eventList = locations.map((location) => ({
    [location]: [
      events.filter((event) => event.full_slug.split('archived-events/').pop().split('/')[0] === city).map((event) => ({
        theme: event.content.theme.name,
        date: event.content.date.replace(' 00:00','').replace(/-/g, '.').split('.').reverse().join('.'),
        link:`${event.full_slug.split('archived-events/').pop().split('/')[0]}-${event.content.theme.name}`,
      }))
    ]
  }))

  return (
    <div id="archive">
      <ArchiveLocations locations={locations} lang={lang} selectedLocation={selectedLocation} setSelectedLocation={setSelectedLocation}/>
      <ArchiveList selectedLocation={selectedLocation}/>
    </div>
  )
}

export async function getServerSideProps({ preview = null }) {
  const themes = (await getThemes(preview)) || []
  const events = (await getEventList(preview)) || []
  return {
      props: { themes, events },
  }
}

export default Archive
