import { getHome } from '../lib/api'
import { useEffect } from 'react';
import { render } from "storyblok-rich-text-react-renderer"

const Home = ({content }) => {

  useEffect(() => {
      document.querySelector("h1").style.opacity="1";
      setTimeout(() => {
        document.querySelector(".background").style.opacity="1";
        // document.querySelector("#body2").style.opacity="1";}, 1200);
      })
  }, [content]);

  return (
    <div id="home">
      <div className="background">
        <div className="background__text">{render(content.first_block)}</div>
      </div>
      <div>{render(content.content)}</div>
    </div>
  )
}

export async function getServerSideProps({ preview = null, params}) {
  console.log(params);
  const content = (await getHome(preview)) || []
  return {
      props: { content },
  }
}

export default Home
