import React, { useEffect, useState } from 'react';
import { Route, Switch, withRouter } from 'react-router-dom';
import styled from "styled-components";
import { TransitionGroup, CSSTransition } from "react-transition-group";

import Introduction from '../Introduction';
import MainText from '../MainText';

// == Import
// import './style.css';


// == Composant
const Container = ({ location, lang, content }) => {

  useEffect(() => {
    // if(document.body.clientWidth < 700) {
      document.querySelector('.play-button--black').style.animation = 'unset';
      setTimeout(() => {document.querySelector('.play-button--black').style.animation = 'grow--mobile 2.5s ease-in-out';}, 500);
    // }
  }, [location]);

  const pages = ['storyfeld','warum','wie','wer','wo','premiere'];

  return (
    <Wrapper>
      <TransitionGroup className="transition-group">
        <CSSTransition
          key={location.key}
          timeout={{ enter: 600, exit: 300 }}
          classNames={'fade'}
        >
           <Switch location={location}>
              <Route exact path="/">
                <Introduction />
              </Route>
              {
                pages.map((page, index) => {

                  const next = index === pages.length - 1 ? 0 : index + 1;

                  return (
                    <Route path={`/${page}`}>
                      <MainText title={content[lang].titles[index]} content={content[lang].content[page]} next={pages[next]} location={page} lang={lang}/>
                    </Route>
                )})
              }
          </Switch>
        </CSSTransition>
      </TransitionGroup>
    </Wrapper>
  );
};

const Wrapper = styled.div`
    .fade-enter {
        opacity: 0.01;
    }
    .fade-enter.fade-enter-active {
        opacity: 1;
        transition: opacity 600ms ease-in;
        transition-delay: 300ms;
    }
    .fade-exit {
        opacity: 1;
    }

    .fade-exit.fade-exit-active {
        opacity: 0.01;
        transition: opacity 300ms ease-in;
    }
`;

// == Export
export default withRouter(Container);
