const ArchiveLocations= ({locations, lang, selectedLocation, setSelectedLocation}) => {

    return (
      <nav className="archive__locations">
        <ul>
          {
            locations.map((location, index) => (
              <li className={`archive__locations__city${selectedLocation === index ? ' archive__locations__city--selected':''}`} onClick={() => setSelectedLocation(index)}>{location.name}</li>
            ))
          }
        </ul>
      </nav>
    )
  }

  export default ArchiveLocations
