import { render } from "storyblok-rich-text-react-renderer"

const StoryRead = ({ text, photo }) => {

  const [textIndex, setTextIndex] = useState(0);

  const changeText = (index) => {
    setTextIndex(index);
    // document.querySelector('.story__read audio').load();
  }

    return (
      <div className="story__read">
          <img src={photo} />
          <div>{render(text[0].content)}</div>
          {
          text.length > 0 && (
            <div className="story__read__lang">
              {
                text.map((lang, index) => (
                  <div key={lang} className={`story__read__lang__option story__read__lang__option--${lang} ${textIndex === index ? ' story__read__lang__option--active':''}`} onClick={() => changeText(index)}>{lang.alt}</div>
                ))
              }
            </div>
          )
        }
      </div>
    );
  };

  export default StoryRead;
