import Link from 'next/link'
import { useEffect, useState } from 'react'

const ArchiveList = ({ eventList, lang }) => {

  const soon = {
    de: 'Bald',
    en: 'Soon',
    ar: 'هكذا'
  }
  return (
    <div className="archive__list">
      {eventList.map((event) => {
        var today = new Date();
        var eventdate = new Date(event[0].date.substring(6, 10) , event[0].date.substring(3, 5), event[0].date.substring(0, 2));
        console.log(eventdate);
        if(today < eventdate) { console.log(event[0].date, 'FUTURE')}
        return( 
        <a className="archive__list__event" href={`/stories/${event[0].link}`}>
          <img src={event[0].thumbnail} />
          <p>
            {today < eventdate && (`${soon[lang]}: `)}
            {event[0].theme}<br/>{event[0].date}
          </p>
        </a>
      )})}
    </div>
  )
}

export default ArchiveList
