const EventToolBar = ({ media, setMedia }) => {

    const modes = ['read','watch','listen','photos','surprise']

    return (
      <div className="event__toolbar">
          {
              modes.map((mode) => (
                <button className={`archive__toolbar__button${media === mode ? ' archive__toolbar__button--selected':''}`} onClick={() => setMedia('place')}>{mode}</button>
              ))
          }
      </div>
    );
  };

  export default EventToolBar;
