import Link from 'next/link'
import { useEffect, useState } from 'react'

const ArchiveList = ({ eventList }) => {

  console.log(eventList)
  return (
    <div className="archive__list">
      {eventList.map((event) => {
        var today = new Date().getTime();
        var eventdate = new Date(event[0].date.substring(0, 2) , event[0].date.substring(2, 2), event[0].date.substring(5, 4)).getTime();
        console.log(event[0].date.substring(0, 2), event[0].date.substring(2, 2), event[0].date.substring(6, 4));
        return( 
        <a className="archive__list__event" href={`/stories/${event[0].link}`}>
          <img src={event[0].thumbnail} />
          <p>{event[0].theme}<br/>{event[0].date}</p>
        </a>
      )})}
    </div>
  )
}

export default ArchiveList
