import Link from 'next/link'
import $ from 'jquery';

const Introduction = ({ content }) => {

  const showArrow = (event) => {
    document.querySelector('.arrow-cursor').style.display = 'block';
    arrowCursor(event)
  }

  const arrowCursor = (e) => {
    $('.arrow-cursor').offset({
      left: e.pageX - 50,
      top: e.pageY - 20
    });
  }

  return (
    <div>
      <section className="introduction">
        <video className="introduction__video" autoPlay>
          <source src={content.Introduction_video.filename} />
        </video>

        <h1 className="introduction__title introduction__title--mobile">S T O R Y F E L D</h1>
        <h1 className="introduction__title introduction__title--desktop">S T O R Y F E L D</h1>
        <p className="introduction__subtitle introduction__subtitle--mobile">{content.Subtitle}</p>
        <p className="introduction__subtitle introduction__subtitle--desktop">{content.Subtitle}</p>

        <Link href={`/archive${content.Storylink.url}`} onClick={(event) => showArrow(event)}>
          <a className="introduction__link introduction__link--story">Go to story</a></Link>
        <Link href="/home/was" onClick={(event) => showArrow(event)}><a className="introduction__link introduction__link--story">About Storyfeld</a></Link>
      </section>
    </div>
  );
};

export default Introduction;
