import { useState } from "react";

const StoryListen = ({ audio }) => {

    const [audioIndex, setAudioIndex] = useState(0);

    return (
      <div className="story__listen">
          <audio controls>
          <source src={audio[audioIndex].filename} />
          </audio>
      </div>
    );
  };

  export default StoryListen;
